prompt --application/shared_components/reports/report_queries/my_allocated_ideas
begin
wwv_flow_api.create_shared_query(
 p_id=>wwv_flow_api.id(63417409309786520467)
,p_name=>'My Allocated Ideas'
,p_query_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'    SELECT  a.GI_REF AS Reference_ID,',
'        a.PARTICIPATION_LEVEL,',
'        a.TITLE_OF_IDEA,',
'        b.FIRST_NAME || '' '' || b.LAST_NAME AS INITIATOR_NAME,',
'        a.CURRENT_STATUS AS Status,',
'        a.DATE_LOGGED,',
'        a.REVIEW_DATE',
'FROM GI_IDEAS a, GI_USERS b',
'WHERE b.USER_ID = a.INITIATOR_ID AND',
'a.ALLOCATED_TO = TO_CHAR((SELECT USER_ID FROM GI_USERS WHERE UPPER(:APP_USER) = UPPER(USER_NAME)))',
'AND a.ALLOCATED_TO IS NOT NULL',
'ORDER BY a.REVIEW_DATE ASC'))
,p_report_layout_id=>wwv_flow_api.id(63656903154488260339)
,p_format=>'RTF'
,p_output_file_name=>'My Allocated Ideas'
,p_content_disposition=>'ATTACHMENT'
);
wwv_flow_api.create_shared_query_stmnt(
 p_id=>wwv_flow_api.id(65942614443548075976)
,p_shared_query_id=>wwv_flow_api.id(63417409309786520467)
,p_sql_statement=>wwv_flow_string.join(wwv_flow_t_varchar2(
'        SELECT  ',
'        (SELECT FIRST_NAME || '' '' || LAST_NAME FROM GI_USERS WHERE USER_NAME = :APP_USER) AS "Allocated to",',
'        a.GI_REF AS REFERENCE_ID,',
'        a.PARTICIPATION_LEVEL,',
'        a.TITLE_OF_IDEA,',
'        a.CURRENT_STATUS AS Status,',
'        a.LAST_UPDATE_DATE as "Last Updated",',
'        a.REVIEW_DATE',
'FROM GI_IDEAS a, GI_USERS b',
'WHERE ',
'    b.USER_ID = a.INITIATOR_ID ',
'    AND',
'    a.ALLOCATED_TO = TO_CHAR((SELECT USER_ID FROM GI_USERS WHERE (:APP_USER) = (USER_NAME)))',
'AND ',
'    a.ALLOCATED_TO IS NOT NULL',
'ORDER BY ',
'    a.REVIEW_DATE ASC'))
);
end;
