'BEGIN',
'    UPDATE GI_IDEAS',
'            /*',
'                Table columns DATE_LOGGED, INITIATOR_ID, CREATION_DATE and CREATED_BY',
'                are populated in the INSERT statement from the home page (Page 1).',
'            */',
'        SET',
'            GI_REF = :P4_GI_IDEA_REF,',
'            ',
'            /*',
'                CASE statement accounts for the different options in the first drop down.',
'            */',
'            AREA_AFFECTED = CASE',
'            WHEN :P4_IDEA_AREA = ''My Business Area'' THEN',
'           -- AO',
'            ''My Business Area''',
'           --:P4_BUSINESS_AREA',
'            WHEN :P4_IDEA_AREA = ''Other Business Area'' THEN',
'            ''Other Business Area''',
'            --:P4_IDEA_AREA_OTHER',
'            WHEN :P4_IDEA_AREA = ''My Office'' THEN',
'            ''My Office''',
'            --:P4_OFFICE',
'            ',
'            WHEN :P4_IDEA_AREA = ''IT Development'' THEN',
'            ''IT Development''',
'            ',
'            ELSE',
'            :P4_IDEA_AREA END,',
'            ',
'            -- AO ADDING AREA_AFFECTED_DETAIL INTO UPDATE',
'            AREA_AFFECTED_DETAIL = CASE',
'            WHEN :P4_IDEA_AREA = ''My Business Area'' THEN',
'            :P4_BUSINESS_AREA',
'            ',
'            WHEN :P4_IDEA_AREA = ''Other Business Area'' THEN',
'            :P4_IDEA_AREA_OTHER',
'            ',
'            WHEN :P4_IDEA_AREA = ''My Office'' THEN',
'            :P4_OFFICE',
'            ',
'            WHEN :P4_IDEA_AREA = ''IT Development'' THEN',
'            :P4_IT_DEVELOPMENT',
'                        ',
