'DECLARE',
'',
'CURRENT_STATUS VARCHAR2(100);',
'FULL_NAME VARCHAR2(1000);',
'EMAIL_BODY VARCHAR2(1000);',
'b VARCHAR2(1000);',
'GI_REF VARCHAR2(1000);',
