'',
'BEGIN',
'',
'-- THIS IS FOR WHEN THE CURRENT STATUS OF THE IDEA IS CHANGED, HOWEVER, IT HAS NOT BEEN ALLOCATED OR GIVEN A REVIEW DATE OR PARTICIPATION LEVEL',
'',
'CURRENT_STATUS := NULL;',
'',
'GI_REF := :P6_GI_IDEA_REF;',
'',
'SELECT UPPER(C.USER_NAME) INTO EMAIL',
'    FROM GI_USERS C',
'WHERE C.USER_ID = (SELECT INITIATOR_ID FROM GI_IDEAS WHERE :P6_GI_IDEA_REF = GI_REF);',
'',
'',
'SELECT CURRENT_STATUS INTO CURRENT_STATUS FROM GI_IDEAS WHERE :P6_GI_IDEA_REF = GI_REF;',
'',
'',
'-- INSERT into GI_STATUS_HISTORY to record status when current status is changed',
'',
'IF (UPPER(:P6_CURRENT_STATUS) != UPPER(CURRENT_STATUS)) THEN',
'    INSERT INTO GI_STATUS_HISTORY',
'        (GI_REF, STATUS_CHANGED_TO, STATUS_CHANGED_FROM, DATE_CHANGED, CHANGED_BY, CREATION_DATE, CREATED_BY, LAST_UPDATED_BY, LAST_UPDATE_DATE)',
'    VALUES',
'        (:P6_GI_IDEA_REF, :P6_CURRENT_STATUS, CURRENT_STATUS, sysdate, :P6_USER_ID, sysdate, :P6_USER_ID, :P6_USER_ID, sysdate);',
'END IF;',
'    ',
'    -- THIS IS TO UPDATE REVIEW DATE SO WHEN STATUS IS CHANGED TO BENEFITS REALISED THE DISABLED REVIEW DATE WILL SAVE AS NULL',
'    IF upper(:P6_CURRENT_STATUS) != upper(CURRENT_STATUS) AND  upper(CURRENT_STATUS) != upper(''AWAITING APPROVAL'') THEN ',
'    UPDATE GI_IDEAS ',
'        SET CURRENT_STATUS = :P6_CURRENT_STATUS, REVIEW_DATE = :P6_REVIEW_DATE',
'    WHERE upper(:P6_GI_IDEA_REF) = upper(GI_REF);',
'    END IF;',
'    ',
'-- IDEA IN TABLE IS AWAITING APPROVAL AND IS NOW BEING APPROVED IF SO THEN EMAIL IF NOT JUST UPDATE',
'IF upper(:P6_CURRENT_STATUS) != upper(CURRENT_STATUS) AND  upper(CURRENT_STATUS) = upper(''AWAITING APPROVAL'') THEN',
'    ',
'    UPDATE GI_IDEAS ',
'        SET CURRENT_STATUS = :P6_CURRENT_STATUS',
'    WHERE upper(:P6_GI_IDEA_REF) = upper(GI_REF); ',
'    ',
'    SELECT a.FIRST_NAME  ||  '' ''  || a.LAST_NAME INTO FULL_NAME',
'        FROM GI_USERS a ',
'    WHERE a.USER_ID =  (SELECT INITIATOR_ID FROM GI_IDEAS WHERE :P6_GI_IDEA_REF = GI_REF);',
'',
'',
'    b :=  FULL_NAME || ''.<p>'' || GI_REF || '', '' || :P6_IDEA_TITLE || ''<p>Thank you for your idea, it has now been approved and can be viewed and commented on by other users.</p>'';',
'',
'',
'    EMAIL_BODY := ''<p>Dear, '' || b || ''</p>'';',
'',
'    APEX_MAIL.SEND(',
'        p_to        => EMAIL,',
'            p_from      => ''noreply@laadev.co.uk'',',
'            p_subj      => ''No Reply'',',
'            p_body      => EMAIL_BODY,',
'            p_body_html => EMAIL_BODY);',
'',
'    APEX_MAIL.PUSH_QUEUE;',
'',
'ELSE ',
'    UPDATE GI_IDEAS ',
'        SET CURRENT_STATUS = :P6_CURRENT_STATUS',
'    WHERE upper(:P6_GI_IDEA_REF) = upper(GI_REF);',
'END IF;',
'END;',
'',
''))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(60142626934374912886)
,p_process_when=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'',
'ROLE_ID NUMBER;',
'CURRENT_STATUS VARCHAR2(100);',
'PARTICIPATION_LEVEL VARCHAR2(100);',
'PAT VARCHAR2(100);',
'PAT1 VARCHAR2(100);',
'REVIEW_DATE DATE;',
'REV DATE;',
'REV1 DATE;',
'ALLOCATED_TO NUMBER;',
'ALLO NUMBER;',
'ALLO1 NUMBER;',
'',
'BEGIN',
'',
'SELECT CURRENT_STATUS, ALLOCATED_TO, REVIEW_DATE, PARTICIPATION_LEVEL INTO CURRENT_STATUS, ALLOCATED_TO,REVIEW_DATE, PARTICIPATION_LEVEL FROM GI_IDEAS WHERE UPPER(GI_REF) = UPPER(:P6_GI_IDEA_REF);',
'',
'-- FOUND OUT THE HARD WAY THAT COMPARING NULL VALUES DOES NOT WORK IN ORACLE E.G. NULL = NULL BRINGS BACK FALSE >:(',
'-- SO IF THERE IS ONLY A STATUS CHANGE THEN I NEED TO CHECK THAT ALLOCATED AND REVIEW DATE AND PARTICIPATION LEVEL HASN''T CHANGED',
'-- SO REVIEW_DATE = :P6_REVIEW_DATE RIGHT? WELL IF THE FIELD IS NULL AND THE TABLE IS NULL THEN IT SHOULD BRING BACK TRUE BUT NULL = NULL BRINGS FALSE',
'-- SO I SAID WHEN BOTH ARE NULL THEN IT''S COMPARING ''YES'' = ''YES'' WHICH IS TRUE',
'',
'-- I NEED TO CHECK IF JUST THE CURRENT STATUS HAS BEEN CHANGED AND NOTHING ELSE',
'PAT := NVL(PARTICIPATION_LEVEL, ''YES'');',
'PAT1 := NVL(:P6_PARTICIPATION_LEVEL, ''YES'');',
'REV  := NVL(REVIEW_DATE, TO_DATE(''010101'',''DDMMYY''));',
'REV1  := NVL(:P6_REVIEW_DATE, TO_DATE(''010101'',''DDMMYY''));',
'ALLO  := NVL(ALLOCATED_TO, 0);',
'ALLO1  := NVL(:P6_ALLOCATED_TO, 0);',
'',
'SELECT ROLE_ID INTO ROLE_ID FROM GI_USERS WHERE UPPER(:APP_USER) = UPPER(USER_NAME);',
'',
'-- HAS THE STATUS BEEN CHANGED THEN ALLOW THIS PROCCESS IF NOT DON''T ALLOW ',
'-- IS THE ROLE ADMIN THEN...',
'IF UPPER(:P6_CURRENT_STATUS) != UPPER(''BENEFITS REALISED'') AND UPPER(:P6_CURRENT_STATUS) != UPPER(''NOT PROCEEDING'') THEN',
'',
'    IF (UPPER(:P6_CURRENT_STATUS) != UPPER(CURRENT_STATUS) AND PAT = PAT1 AND ALLO = ALLO1 AND REV = REV1)',
'',
'    THEN',
'        ',
'        IF ROLE_ID = 2 THEN RETURN TRUE;',
'         ELSE ',
'        ',
'         RETURN FALSE;',
'        END IF;',
'    ',
'    ELSE',
'        RETURN FALSE;',
'    END IF;',
'',
'ELSE',
'-- NO COMPARING REVIEW DATE BECAUSE BENEFITS REALISED WILL CHANGE REVIEW DATE TO NULL BECAUSE IT UPDATES A DISABLED FIELD',
'    IF UPPER(:P6_CURRENT_STATUS) != UPPER(CURRENT_STATUS) AND ( PAT = PAT1 AND ALLO = ALLO1)',
'    THEN',
'',
'        IF ROLE_ID = 2 THEN ',
'            RETURN TRUE;',
'        ELSE ',
'            RETURN FALSE;',
'        END IF;',
'    ',
'    ELSE',
'        RETURN FALSE;',
'    END IF;',
'',
'END IF;',
'END;'))
,p_process_when_type=>'FUNCTION_BODY'
,p_process_success_message=>'Successfully updated idea'
);
