'',
'         --Initial measurements update. When moving idea status to ''Implemented''',
'IF :P6_CURRENT_STATUS = ''Implemented'' THEN',
'UPDATE GI_IDEAS SET',
'                    INI_COMPLAINT_COUNT = :P6_INI_COMPLAINT_COUNT,',
'                    INI_ERROR_COUNT = :P6_INI_ERROR_COUNT,',
'                    INI_REJECTS = :P6_INI_REJECTS_COUNT,',
'                    INI_RESOURCE_HOURS = :P6_INI_RESOURCE_HOURS,',
'                    INI_RISK = :P6_INI_RISK,',
'                    INI_IMPACT = :P6_INI_IMPACT,',
'                    INI_STAFF_SURVEY_RESULTS = :P6_INI_TEMP_RESULTS,',
'                    INI_COST = :P6_INI_COST',
'                WHERE UPPER(:P6_GI_IDEA_REF) = UPPER(GI_REF);',
'END IF;',
'',
'                 --Post measurements update. When moving idea status to ''Interim Benefits''',
'IF :P6_CURRENT_STATUS = ''Interim Benefits'' THEN',
'UPDATE GI_IDEAS SET',
'                    POST_COMPLAINT_COUNT = :P6_POST_COMPLAINT_COUNT,',
'                    POST_ERROR_COUNT = :P6_POST_ERROR_COUNT,',
