'END IF;',
'    ',
'',
'-- THE IF STATEMENT IS THERE TO CHECK THAT IF THE THERE''S BEEN A CHANGE FROM WHAT''S IN THE DATABASE',
'',
'IF (UPPER(CURRENT_STATUS) <> UPPER(:P6_CURRENT_STATUS) OR REVIEW_DATE <> :P6_REVIEW_DATE) THEN',
'',
'    IF (UPPER(CURRENT_STATUS) <> UPPER(:P6_CURRENT_STATUS) AND REVIEW_DATE <> :P6_REVIEW_DATE) THEN',
'',
'        EMAIL_BODY := ''The status of this idea has been changed to '' ||  :P6_CURRENT_STATUS || '' and the review date of this idea has been changed to '' || :P6_REVIEW_DATE ||',
'        '' by '' || USER_NAME || ''<p>GI Ref: '' || :P6_GI_IDEA_REF || '' '' || ''</p><p>GI Title: '' || TITLE || ''</p>'';',
'        ',
'    ELSIF (REVIEW_DATE != :P6_REVIEW_DATE) THEN',
'    ',
'         EMAIL_BODY := ''The review date of this idea has been changed to '' || :P6_REVIEW_DATE ||',
'        '' by '' || USER_NAME || ''<p>GI Ref: '' || :P6_GI_IDEA_REF || '' '' || ''</p><p>GI Title: '' || TITLE || ''</p>'';',
'',
'    ELSIF (UPPER(CURRENT_STATUS) != UPPER(:P6_CURRENT_STATUS)) THEN',
'    ',
'         EMAIL_BODY := ''The status of this idea has been changed to '' ||  :P6_CURRENT_STATUS ||',
'         '' by '' || USER_NAME || ''<p>GI Ref: '' || :P6_GI_IDEA_REF || '' '' || ''</p><p>GI Title: '' || TITLE || ''</p>'';',
'',
'    END IF;',
'    ',
'UPDATE GI_IDEAS SET CURRENT_STATUS = :P6_CURRENT_STATUS, ',
'                    REVIEW_DATE = :P6_REVIEW_DATE,',
'                    LAST_UPDATE_DATE = SYSDATE,',
'                    LAST_UPDATED_BY = USER_ID',
'                 -- GI lEAD SHOULD NOT BE ABLE TO UPDATE ALLOCATED TO OR PARTICIPATION LEVEL',
'                 --   ALLOCATED_TO = :P6_ALLOCATED_TO,',
'                 --   PARTICIPATION_LEVEL = :P6_PARTICIPATION_LEVEL',
'                 WHERE UPPER(:P6_GI_IDEA_REF) = UPPER(GI_REF);',
'',
'        -- taken from here to line 16 - abdi',
'',
' APEX_MAIL.SEND(',
'                        p_to        => ''ABDIRIZAK.OBSIYE@JUSTICE.GSI.GOV.UK''/*''GreatIdea@legalaid.gsi.gov.uk''*/,',
'                        p_from      => ''noreply@laadev.co.uk'',',
