wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(48906388465152655383)
,p_process_sequence=>24
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SAVE_AND_UPDATE_PROFILE'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE ',
'b varchar2(1000);',
't varchar2(1000);',
'l_body_html varchar2(1000);',
'FIRSTNAME varchar2(50);',
'LASTNAME varchar2(50);',
'USERNAME varchar2(100);',
'EMAIL_ADDRESS varchar2(100);',
'DIRECTORATE varchar2(100);',
'TELEPHONE_NUMBER varchar2(100);',
'TEAM varchar2(100);',
'BUSINESS_AREA varchar2(100);',
'OFFICE varchar2(100);',
'BEGIN',
'    UPDATE GI_USERS',
'        SET',
'            FIRST_NAME = :P8_FIRST_NAME,',
'            LAST_NAME = :P8_LAST_NAME,',
'            USER_DIRECTORATE = :P8_DIRECTORATE,',
'            USER_CONTACT_NUM = :P8_CONTACT_NUM,',
'            USER_BUSINESS_AREA = :P8_BUSINESS_AREA,',
'            ',
'            USER_BUSINESS_AREA_OTHER = CASE',
'            WHEN :P8_BUSINESS_AREA !=',
'           -- 104 ',
'            (SELECT BA_ID FROM GI_BUSINESS_AREAS WHERE UPPER(BA_NAME) = UPPER(''OTHER'') ) THEN',
'            NULL',
'            ELSE',
'            :P8_BUSINESS_AREA_OTHER END,',
'    ',
'            USER_TEAM = :P8_TEAM,',
'    ',
'            USER_TEAM_OTHER = CASE',
'            WHEN :P8_TEAM != ',
'            --61 ',
'            (SELECT TEAM_ID FROM GI_TEAMS WHERE UPPER(TEAM_NAME) = UPPER(''OTHER'') )',
'            THEN',
'            NULL',
'            ELSE',
'            :P8_TEAM_OTHER END,',
'    ',
'            OFFICE = :P8_OFFICE,',
'            LAST_UPDATE_DATE = SYSDATE,',
'            LAST_UPDATED_BY = (SELECT USER_ID FROM GI_USERS WHERE UPPER(USER_NAME) = UPPER(:APP_USER))',
'        WHERE UPPER(USER_NAME) = UPPER(:APP_USER);',
'        --USER_ID = :P8_USER_ID;',
'        ',
'BEGIN',
'select',
'c.user_name,',
'c.first_name, ',
'c.last_name,',
'c.email_address,',
'c.user_contact_num,',
'c.USER_BUSINESS_AREA_OTHER,',
'c.user_team_other,',
'c.office',
'INTO',
'username,',
'firstname,',
'lastname,',
'email_address,',
'telephone_number,',
'b,',
't,',
'office',
'FROM',
'GI_USERS c',
'--where',
'--upper(:P8_USER_NAME) = upper(c.user_name);',
'WHERE UPPER(USER_NAME) = UPPER(:APP_USER);',
'',
'-- below i''m bringing back team businessarea and directorate names as the above brings back an id number',
'SELECT',
'    t.TEAM_NAME',
'    INTO ',
'    team',
'FROM',
'    GI_TEAMS t,',
'    GI_USERS u',
'WHERE',
'    u.USER_TEAM = t.TEAM_ID',
'AND',
'    u.USER_ID = (SELECT USER_ID FROM GI_USERS WHERE UPPER(USER_NAME) = UPPER(:APP_USER));',
' ',
'SELECT',
'    b.BA_NAME',
'INTO business_area',
'FROM',
'    GI_BUSINESS_AREAS b,',
'    GI_USERS u',
'WHERE',
'    u.USER_BUSINESS_AREA = b.BA_ID',
'AND',
'    u.USER_ID = (SELECT USER_ID FROM GI_USERS WHERE UPPER(USER_NAME) = UPPER(:APP_USER));',
'    ',
'SELECT',
'    d.DIR_NAME',
'INTO',
'    directorate',
'FROM',
'    GI_DIRECTORATE d,',
'    GI_USERS u',
'WHERE',
'    u.USER_DIRECTORATE = d.DIR_ID',
'AND',
'    u.USER_ID = (SELECT USER_ID FROM GI_USERS WHERE UPPER(USER_NAME) = UPPER(:APP_USER));',
' ',
'IF ',
'',
':P8_TEAM = 61 OR :P8_BUSINESS_AREA = 104',
'',
'THEN ',
'',
'l_body_html := ''Hello Great Ideas Team,<br/><br/>This user has changed their business area or team under "other"<br/><br/>Username: '' || username ||',
'                ''<br/>First Name: '' || FIRSTNAME || ''<br/>Last Name: '' || LASTNAME || ''<br/>Email Address: ''|| ',
'                email_address ||''<br/>Telephone Number: ''|| telephone_number || ''<br/>Directorate: '' || directorate ||''<br/>Business Area: '' || business_area',
'                || ''<br/>Other Business Area: '' || b || ''<br/>Team: ''|| team || ''<br>Other Team: '' || t || ''<br/>Office: '' || office;',
'',
'    APEX_MAIL.SEND(',
'        p_to           => ''GreatIdea@legalaid.gsi.gov.uk'',/*''GreatIdea@legalaid.gsi.gov.uk''*/',
'        p_from         => ''noreply@laadev.co.uk'',',
'        p_subj         => ''APEX_MAIL: DO NOT REPLY TO THIS MESSAGE'',',
'        p_body         => ''DO NOT REPLY'',',
'        p_body_html    => l_body_html);',
'APEX_MAIL.PUSH_QUEUE;',
'',
'END IF;',
'',
'END;',
'',
'',
'    COMMIT;',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(48906445610188274527)
,p_process_when_type=>'NOT_DISPLAYING_INLINE_VALIDATION_ERRORS'
,p_process_success_message=>'User details have been successfully updated.'
);
