wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(71575533061700897989)
,p_process_sequence=>5
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'SEND_EMAILS'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/*',
'    [IMPORTANT]',
'    This process must happen before the UPDATE statements in the ''ADMIN_SAVE_ALL'' and ''GI_LEAD_SAVE'' processes',
'    Hence why the sequence of this process is 5',
'*/',
'DECLARE',
'-- ''DB_'' variables store an ideas status data that is already in the database before the update processes',
'    DB_CURRENT_STATUS VARCHAR2(100);',
'    DB_ALLOCATED_USER VARCHAR2(100);',
'    DB_PARTICIPATION_LEVEL VARCHAR2(100);',
'    DB_REVIEW_DATE DATE;',
'    DB_IDEA_TITLE VARCHAR2(100);',
'-- ''INITIATOR_'' variables store data relating to the initiator of the idea',
'    INITIATOR_FULL_NAME VARCHAR2(1000);',
'    INITIATOR_EMAIL VARCHAR2(100);',
'    INITIATOR_EMAIL_BODY VARCHAR2(2000);',
'-- ''LEADER_'' variables store data relating to the assigned leader of the idea',
'    LEADER_FULL_NAME VARCHAR2(1000);',
'    LEADER_EMAIL VARCHAR2(100);',
'    LEADER_EMAIL_BODY VARCHAR2(2000);',
'',
'BEGIN',
'',
'    -- Get status data from database',
'    SELECT',
'        CURRENT_STATUS, ALLOCATED_TO, PARTICIPATION_LEVEL, REVIEW_DATE, TITLE_OF_IDEA',
'    INTO',
'        DB_CURRENT_STATUS, DB_ALLOCATED_USER, DB_PARTICIPATION_LEVEL, DB_REVIEW_DATE, DB_IDEA_TITLE',
'    FROM',
'        GI_IDEAS',
'    WHERE',
'        GI_REF = :P15_GI_IDEA_REF;',
'',
'    -- Get initiator details',
'    SELECT',
'        INITCAP(FIRST_NAME) || '' '' || INITCAP(LAST_NAME), EMAIL_ADDRESS',
'    INTO',
'        INITIATOR_FULL_NAME, INITIATOR_EMAIL',
'    FROM',
'        GI_USERS',
'    WHERE',
'        :P15_USER_ID = USER_ID;',
'',
'/*',
'    [ERROR SOLVED]',
'    ''ORA-01403: no data found''',
'    '':P15_ALLOCATED_TO'' and '':P15_PARTICIPATION_LEVEL'' are disabled for GI Leads meaning they return NULL regardless of what they display',
'*/',
'    -- Get allocated user details',
'    SELECT',
'        INITCAP(FIRST_NAME) || '' '' || INITCAP(LAST_NAME), EMAIL_ADDRESS',
'    INTO',
'        LEADER_FULL_NAME, LEADER_EMAIL',
'    FROM',
'        GI_USERS',
'    WHERE',
'        -- Since '':P15_ALLOCATED_TO'' is disabled for GI Leads and will always be NULL NVL() will instead use what is already in the DB',
'        -- May cause problems if what is in the DB is also NULL',
'    NVL(:P15_ALLOCATED_TO, NVL(DB_ALLOCATED_USER, 1)) = USER_ID;',
'',
'',
'    -- Send email to initiator if any one of these fields have been changed',
'    IF',
'        :P15_CURRENT_STATUS <> DB_CURRENT_STATUS',
'        OR',
'        :P15_ALLOCATED_TO <> NVL(DB_ALLOCATED_USER, 0)',
'        OR',
'        :P15_PARTICIPATION_LEVEL <> NVL(DB_PARTICIPATION_LEVEL, 0)',
'        OR',
'        :P15_REVIEW_DATE <> NVL(DB_REVIEW_DATE, sysdate)',
'    THEN',
'',
'        -- Start constructing email body for the email being sent to the idea initiator',
'        INITIATOR_EMAIL_BODY := ''<p>Dear '' || INITIATOR_FULL_NAME || '',</p><p>This is an automated email to inform you that your great idea, <b>''',
'            -- '':P15_IDEA_TITLE'' is disabled for GI Leads',
'            || :P15_GI_IDEA_REF || '' - '' || NVL(:P15_IDEA_TITLE, DB_IDEA_TITLE) || ''</b>, has been updated.</p><p></p>'';',
'',
'        -- If the current status has been changed',
'        IF',
'            :P15_CURRENT_STATUS <> DB_CURRENT_STATUS',
'        THEN',
'            -- If the current status is being moved from ''Awaiting Approval'' to ''Approved''',
'            IF',
'                :P15_CURRENT_STATUS = ''Approved''',
'            AND',
'                DB_CURRENT_STATUS = ''Awaiting Approval''',
'            THEN',
'                INITIATOR_EMAIL_BODY := INITIATOR_EMAIL_BODY || ''<p>Thank you for your idea, it has now been approved and can be viewed and commented on by other users.</p>'';',
'            ELSE',
'                INITIATOR_EMAIL_BODY := INITIATOR_EMAIL_BODY || ''<p>This idea has been moved to the <b>'''''' || :P15_CURRENT_STATUS || ''''''</b> stage.</p>'';',
'            END IF;',
'        END IF;',
'',
'        -- If the assigned leader or participation level has been changed',
'        IF',
'            :P15_ALLOCATED_TO <> NVL(DB_ALLOCATED_USER, 0) -- If ''DB_ALLOCATED_USER'' stores a NULL value then the IF statement breaks which is why NVL() is used',
'            OR',
'            :P15_PARTICIPATION_LEVEL <> NVL(DB_PARTICIPATION_LEVEL, 0)',
'        THEN',
'            -- '':P15_PARTICIPATION_LEVEL'' is disabled for GI Leads',
'            INITIATOR_EMAIL_BODY := INITIATOR_EMAIL_BODY || ''<p>'' || LEADER_FULL_NAME || '' has been assigned to <b>''',
'                || LOWER(NVL(:P15_PARTICIPATION_LEVEL, DB_PARTICIPATION_LEVEL)) || ''</b> this idea.</p>'';',
'        END IF;',
'',
'        -- If the review date has been changed',
'        IF',
'            :P15_REVIEW_DATE <> NVL(DB_REVIEW_DATE, sysdate) -- If ''DB_REVIEW_DATE'' is NULL then sysdate is used since the user cannot put the current date into the field',
'        THEN',
'            INITIATOR_EMAIL_BODY := INITIATOR_EMAIL_BODY || ''<p>Next review date: <b>'' || :P15_REVIEW_DATE || ''</b>.</p>'';',
'        END IF;',
'',
'        -- Send email to initiator',
'        APEX_MAIL.SEND(',
'            p_to        => INITIATOR_EMAIL,',
'            p_from      => ''GreatIdea@justice.gov.uk'',',
'            p_subj      => ''No Reply'',',
'            p_body      => INITIATOR_EMAIL_BODY,',
'            p_body_html => INITIATOR_EMAIL_BODY);',
'        APEX_MAIL.PUSH_QUEUE;',
'    END IF;',
'',
'',
'    -- If the assigned lead has been changed',
'    IF',
