,p_display_sequence=>20
,p_include_in_reg_disp_sel_yn=>'Y'
,p_region_template_options=>'#DEFAULT#:t-Region--scrollBody'
,p_component_template_options=>'#DEFAULT#:t-Report--altRowsDefault:t-Report--rowHighlight:t-Report--noBorders'
,p_display_point=>'BODY'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'-- look up value is the by statuses depending on the filter fields',
'c.LOOKUP_VALUE AS "STATUS",',
'COUNT(c.LOOKUP_VALUE) AS "COUNT"',
'FROM GI_USERS a',
',GI_LOOKUP_VALUES c',
',GI_IDEAS b',
'WHERE',
'',
'SUBMITTED_DATE IS NOT NULL ',
'',
'AND',
'     -- if it is not null then ''where :p16_status = look up value''',
' (NVL(UPPER(:P16_STATUS),UPPER(b.CURRENT_STATUS))) = UPPER(c.LOOKUP_VALUE)',
'',
'AND ',
'     -- date between start and end else if it''s null then bring back everything',
'(CASE WHEN TRIM(:P16_START_DATE) IS NOT NULL AND TRIM(:P16_END_DATE) IS NOT NULL AND to_date(:P16_START_DATE, ''dd/mon/yyyy'')  = to_date(b.SUBMITTED_DATE,''dd/mon/yyyy'') then 1 else',
'            ((CASE WHEN (TRIM(:P16_START_DATE) IS NOT NULL AND TRIM(:P16_END_DATE) IS NOT NULL AND ',
'               ((',
'                  -- b.SUBMITTED_DATE BETWEEN :P16_START_DATE AND :P16_END_DATE',
'                (to_date(:P16_START_DATE,''dd/mon/yyyy'') <= to_date(b.SUBMITTED_DATE,''dd/mon/yyyy'') AND to_date(b.SUBMITTED_DATE,''dd/mon/yyyy'') <= to_date(:P16_END_DATE,''dd/mon/yyyy''))',
'                ))',
'                         ',
'               )',
'     THEN 1 ',
'     ELSE  (CASE WHEN TRIM(:P16_START_DATE) IS NULL OR TRIM(:P16_END_DATE) IS NULL THEN 1 ELSE 2  END)',
'     END) )',
'     END) = 1   ',
'AND',
'',
'-- Filter by benefits tag (checkboxes)',
'',
'(',
'    -- checkbox 1',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,1)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'    ',
'    -- checkbox 2',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,2)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    -- checkbox 3',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,3)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,4)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,5)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,6)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'   ',
'    (CASE WHEN INSTR(UPPER(b.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P16_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P16_BENEFITS_TAG), ''[^:]+'',1,7)) > 0 THEN 1 ELSE 0 END) > 0',
'    ',
'    -- for another checkbox to the benefit tags, copy the two lines above and replace the regexp_substr to e.g. 8 , hopefully the pattern can be seen above.',
'    ',
'    OR :P16_BENEFITS_TAG IS NULL',
')',
'',
'     -- Directorate of the user below',
'AND',
'(NVL(UPPER(:P16_DIRECTORATE),(SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(a.USER_DIRECTORATE)))) = (SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(a.USER_DIRECTORATE))',
'     ',
'     -- Business area of the user below',
'AND',
'(NVL(TRIM(UPPER(:P16_BUSINESS_AREA)),(SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(a.USER_BUSINESS_AREA)))) = (SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(a.USER_BUSINESS_AREA))',
'     -- Team of the user',
'AND',
'(NVL(UPPER(:P16_TEAM),(SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(a.USER_TEAM)))) = (SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(a.USER_TEAM))',
'',
'     -- Area Idea Relates To is below',
'AND',
'--NVL(TRIM(UPPER(:P16_AREA_AFFECTED)),UPPER(b.AREA_AFFECTED)) = UPPER(REPLACE(b.AREA_AFFECTED,''Other'', ''My''))',
'-- Area Idea relates to Filtering',
'(',
'NVL(UPPER(:P16_AREA_AFFECTED), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P16_AREA_AFFECTED, TRIM(UPPER(REPLACE(b.AREA_AFFECTED,''Other'', ''My''))), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P16_RELATESTO_BUSINESS_AREA), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P16_RELATESTO_BUSINESS_AREA, TRIM(UPPER(REPLACE(b.AREA_AFFECTED_DETAIL,''Other'', ''My''))), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P16_RELATESTO_OFFICE), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P16_RELATESTO_OFFICE, TRIM(UPPER(b.AREA_AFFECTED_DETAIL)), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P16_RELATESTO_IT_DEVELOPMENT), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P16_RELATESTO_IT_DEVELOPMENT, TRIM(UPPER(b.AREA_AFFECTED_DETAIL)), b.AREA_AFFECTED)',
')',
'     ',
'AND UPPER(c.LOOKUP_TYPE) = UPPER(''CURRENT STATUS'')',
'AND UPPER(b.CURRENT_STATUS) = UPPER(c.LOOKUP_VALUE)',
'AND b.INITIATOR_ID = a.USER_ID',
'GROUP BY c.LOOKUP_VALUE'))
