,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT DISTINCT',
'    b.GI_REF,',
'    b.BENEFITS_TAGS,',
'    NVL((SELECT u.FIRST_NAME || '' '' || u.LAST_NAME FROM GI_USERS u WHERE to_char(u.USER_ID) = b.ALLOCATED_TO), NULL) AS Allocated_To, -- Is NVL() necessary here? ~CB',
'    b.CURRENT_STATUS,',
'    -- Initial & post measurements ~CB',
'    b.INI_COMPLAINT_COUNT,',
'    b.POST_COMPLAINT_COUNT,',
'    b.INI_ERROR_COUNT,',
'    b.POST_ERROR_COUNT,',
'    b.INI_REJECTS,',
'    b.POST_REJECTS,',
'    b.INI_RESOURCE_HOURS,',
'    b.POST_RESOURCE_HOURS,',
'    b.INI_RISK,',
'    b.POST_RISK,',
'    b.INI_IMPACT,',
'    b.POST_IMPACT,',
'    b.INI_STAFF_SURVEY_RESULTS,',
'    b.POST_STAFF_SURVEY_RESULTS,',
'    b.INI_COST,',
'    b.POST_COST',
'',
'FROM',
'    GI_USERS a,',
'    GI_IDEAS b,',
'    GI_LOOKUP_VALUES c',
'    ',
'WHERE',
'    b.SUBMITTED_DATE IS NOT NULL',
'',
'AND',
'    -- This will exclude any ideas with a current status precedeing ''Implemented'' - ''Not Proceeding''? - Waiting to hear back from business before uncommenting ~CB',
'    b.CURRENT_STATUS NOT IN (''Awaiting Approval'', ''Approved'', ''Investigation'', ''Implementing'')',
'',
'AND',
'    -- Filter by benefit tags (checkboxes)',
'    -- Now with 100% less UPPER() ~CB',
'(',
'    (CASE WHEN INSTR(b.BENEFITS_TAGS, regexp_substr( '':'' || :P23_BENEFITS_TAG || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(BENEFITS_TAGS, regexp_substr(:P23_BENEFITS_TAG, ''[^:]+'',1,1)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(b.BENEFITS_TAGS, regexp_substr( '':'' || :P23_BENEFITS_TAG || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(BENEFITS_TAGS, regexp_substr(:P23_BENEFITS_TAG, ''[^:]+'',1,2)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(b.BENEFITS_TAGS, regexp_substr( '':'' || :P23_BENEFITS_TAG || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(BENEFITS_TAGS, regexp_substr(:P23_BENEFITS_TAG, ''[^:]+'',1,3)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(b.BENEFITS_TAGS, regexp_substr( '':'' || :P23_BENEFITS_TAG || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(BENEFITS_TAGS, regexp_substr(:P23_BENEFITS_TAG, ''[^:]+'',1,4)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(b.BENEFITS_TAGS, regexp_substr( '':'' || :P23_BENEFITS_TAG || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(BENEFITS_TAGS, regexp_substr(:P23_BENEFITS_TAG, ''[^:]+'',1,5)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
