,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select distinct',
'p.GI_REF AS "Reference ID",',
'NVL((Select u.first_name || '' '' || u.last_name from GI_USERS u where u.user_id = p.ALLOCATED_TO), NULL) as Allocated_TO,',
'p.STATUS_CHANGED_TO as "New Status",',
'p.STATUS_CHANGED_FROM as "Previous Status",',
'p.DATE_CHANGED',
'FROM  ',
'-- i''m sorry for anyone that has to read what''s below. i''m partitioning the results by gi_ref and then descending it by row number ',
'-- this means each group of gi_ref will have results in rows 1 to ... until the next gi_ref where it starts from 1 again.',
'(select row_number() over (partition by e.GI_REF order by e.STAT_HIS_ID desc) t, STAT_HIS_ID, GI_REF, STATUS_CHANGED_TO,DATE_CHANGED,',
'     count(*) OVER (partition by e.GI_REF) AS totrows, STATUS_CHANGED_FROM, e.ALLOCATED_TO,e.first_name, e.last_name,e.user_directorate,e.user_team, e.user_business_area, e.initiator_id, e.current_status, e.area_affected, e.area_affected_detail, e.be'
||'nefits_tags from ',
'     (select ROWNUM r, t.DATE_CHANGED, t.STAT_HIS_ID, t.STATUS_CHANGED_TO, t.GI_REF, t.STATUS_CHANGED_FROM, k.first_name, k.last_name,k.user_directorate,k.user_team, k.user_business_area, d.allocated_to, d.initiator_id,d.current_status, d.area_affect'
||'ed,d.area_affected_detail,d.benefits_tags from ',
'      ((SELECT * FROM  GI_STATUS_HISTORY) ORDER BY STAT_HIS_ID) t, GI_IDEAS d, GI_USERS k  WHERE   d.GI_REF =  t.GI_REF and d.initiator_id = k.user_id',
'     )e ',
') p,',
'GI_IDEAS b,',
'GI_USERS a,',
'GI_LOOKUP_VALUES c',
'WHERE ',
'     -- if it is not null then ''where :p16_status = look up value''',
'(NVL(UPPER(:P27_STATUS),UPPER(p.CURRENT_STATUS))) = UPPER(p.CURRENT_STATUS)',
'',
' -- date between start and end else if it''s null then bring back everything',
'AND (CASE WHEN TRIM(:P27_START_DATE) IS NOT NULL AND TRIM(:P27_END_DATE) IS NOT NULL AND to_date(:P27_START_DATE, ''dd/mon/yyyy'')  = to_date(p.DATE_CHANGED,''dd/mon/yyyy'') then 1 else',
'            ((CASE WHEN (TRIM(:P27_START_DATE) IS NOT NULL AND TRIM(:P27_END_DATE) IS NOT NULL AND ',
'               (',
'                   ',
'                 --  (p.DATE_CHANGED BETWEEN :P27_START_DATE AND :P27_END_DATE)',
'               (to_date(:P27_START_DATE,''dd/mon/yyyy'') <= to_date(p.DATE_CHANGED,''dd/mon/yyyy'') AND to_date(p.DATE_CHANGED,''dd/mon/yyyy'') <= to_date(:P27_END_DATE,''dd/mon/yyyy''))',
'               )',
'               )',
'     THEN 1 ',
'     ELSE  (CASE WHEN TRIM(:P27_START_DATE) IS NULL OR TRIM(:P27_END_DATE) IS NULL THEN 1 ELSE 2  END)',
'     END) )',
'     END) = 1',
'     ',
'AND SUBMITTED_DATE IS NOT NULL',
'--and (UPPER(p.CURRENT_STATUS) != UPPER(''UNFINISHED'') AND UPPER(p.CURRENT_STATUS) != UPPER(''CANCEL''))',
'',
'AND',
'',
'',
'-- Filter by benefits tag (checkboxes)',
'',
'(',
'    -- checkbox 1',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,1)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'    ',
'    -- checkbox 2',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,2)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    -- checkbox 3',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,3)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,4)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,5)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,6)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'   ',
'    (CASE WHEN INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P27_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(p.BENEFITS_TAGS), regexp_substr(UPPER(:P27_BENEFITS_TAG), ''[^:]+'',1,7)) > 0 THEN 1 ELSE 0 END) > 0',
'    ',
'    -- for another checkbox to the benefit tags, copy the two lines above and replace the regexp_substr to e.g. 8 , hopefully the pattern can be seen above.',
'    ',
'    OR :P27_BENEFITS_TAG IS NULL',
')',
'   -- Directorate of the user below',
'AND',
'(NVL(UPPER(:P27_DIRECTORATE),(SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(p.USER_DIRECTORATE)))) = (SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(p.USER_DIRECTORATE))',
'     ',
'     -- Business area of the user below',
'AND',
'(NVL(TRIM(UPPER(:P27_BUSINESS_AREA)),(SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(p.USER_BUSINESS_AREA)))) = (SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(p.USER_BUSINESS_AREA))',
'     -- Team of the user',
'AND',
'(NVL(UPPER(:P27_TEAM),(SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(p.USER_TEAM)))) = (SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(p.USER_TEAM))',
'',
'/*',
'     -- Directorate of the user below',
'AND',
'(NVL(UPPER(:P27_DIRECTORATE),UPPER(p.USER_DIRECTORATE))) = UPPER(p.USER_DIRECTORATE)',
'     ',
'     -- Business area of the user below',
'AND',
'(NVL(UPPER(:P27_BUSINESS_AREA),UPPER(p.USER_BUSINESS_AREA))) = UPPER(p.USER_BUSINESS_AREA)',
'',
'     -- Team of the user',
'AND',
'(NVL(UPPER(:P27_TEAM),UPPER(p.USER_TEAM))) = UPPER(p.USER_TEAM)',
'*/',
'',
'     -- Area Idea Relates To is below',
'',
'AND',
'-- Area Idea relates to Filtering',
'(',
'NVL(UPPER(:P27_AREA_AFFECTED), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P27_AREA_AFFECTED, TRIM(UPPER(REPLACE(p.AREA_AFFECTED,''Other'', ''My''))), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P27_RELATESTO_BUSINESS_AREA), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P27_RELATESTO_BUSINESS_AREA, TRIM(UPPER(REPLACE(p.AREA_AFFECTED_DETAIL,''Other'', ''My''))), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P27_RELATESTO_OFFICE), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P27_RELATESTO_OFFICE, TRIM(UPPER(p.AREA_AFFECTED_DETAIL)), b.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P27_RELATESTO_IT_DEVELOPMENT), b.AREA_AFFECTED) ',
'    = ',
'NVL2(:P27_RELATESTO_IT_DEVELOPMENT, TRIM(UPPER(p.AREA_AFFECTED_DETAIL)), b.AREA_AFFECTED)',
')',
'',
'',
'-- p.t is row number partitioned by gi_ref e.g 1 , 2, 3 ,4 and i''m bringing back 1 which is the most recent status change info',
'and p.t = 1',
'AND (NVL(UPPER(:P27_STATUS),UPPER(p.CURRENT_STATUS))) = UPPER(p.CURRENT_STATUS)',
'AND UPPER(c.LOOKUP_TYPE) = UPPER(''CURRENT STATUS'')',
'AND UPPER(p.CURRENT_STATUS) = UPPER(c.LOOKUP_VALUE)',
'order by p.GI_REF',
''))
