,p_display_point=>'BODY'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/*select * from GI_IDEAS*/',
'SELECT ',
'i.GI_REF,',
'       u.FIRST_NAME  ||  '' ''  || u.LAST_NAME as Initiator,',
'       d.DIR_NAME as Directorate,',
'       b.BA_NAME as Business_Area,',
'       t.TEAM_NAME as Team,',
'       o.OFFICE_NAME as Office,',
'       i.SUBMITTED_DATE as Submitted_Date,',
'       i.AREA_AFFECTED as Area_Affected,',
'       i.AREA_AFFECTED_DETAIL as Area_Affected_Detail,',
'       i.TITLE_OF_IDEA as Idea_Title,',
'       i.IDEA as Idea,',
'       i.BENEFITS_TAGS as Benefits,',
'       i.BENEFITS as Benefits_Further_Details,',
'       i.INITIATOR_INVOLVEMENT as Initiator_Involvement,',
'       i.CURRENT_STATUS as Status,',
'       NVL((Select u.first_name || '' '' || u.last_name from GI_USERS u where to_char(u.user_id) = i.ALLOCATED_TO), NULL) as Allocated_TO,',
'      --- (select LOWER(USER_NAME) from GI_USERS a, GI_IDEAS b where GI_REF = ''GI125'' and b.ALLOCATED_TO = a.USER_ID) as Allocated_To,',
'       i.PARTICIPATION_LEVEL as Participation_Level,',
'       i.REVIEW_DATE as Review_Date,',
'       i.INI_COMPLAINT_COUNT as Initial_Complaint_Count, ',
'       i.POST_COMPLAINT_COUNT as Post_Complaint_Count,',
'	i.INI_ERROR_COUNT as Initial_Error_Count, ',
'   i.POST_ERROR_COUNT as Post_Error_Count, ',
'	i.INI_REJECTS as Initial_Rejects, ',
'   i.POST_REJECTS as Post_Rejects,',
'	i.INI_RESOURCE_HOURS as Initial_Resource_Hours,',
'   i.POST_RESOURCE_HOURS as Post_Resource_Hours, ',
'	i.INI_RISK as Initial_Risk,',
'   i.POST_RISK as Post_Risk, ',
'	i.INI_STAFF_SURVEY_RESULTS as Initial_Staff_Survey_Results, ',
'   i.POST_STAFF_SURVEY_RESULTS as Post_Staff_Survey_Result, ',
'	i.INI_COST as Initial_Cost,	',
'	i.POST_COST as Post_Cost, ',
'	i.INI_IMPACT as Initial_Impact, ',
'	i.POST_IMPACT as Post_Impact,',
'       i.VOTE_COUNT as Vote_count,',
'       c.category as Comment_Category,',
'       c.great_Ideas_comments as Comments,',
'       (select u.FIRST_NAME || '' '' || u.LAST_NAME from gi_users u where u.user_id = c.user_id and c.gi_ref = i.GI_REF) as commentor,',
'       c.status_at_comment_time as comment_status,',
'       c.comment_date',
'FROM GI_IDEAS i,',
'     GI_USERS u,',
'     GI_DIRECTORATE d,',
'     GI_BUSINESS_AREAS b,',
'     GI_TEAMS t,',
'     GI_OFFICE o,',
'     GI_COMMENTS c',
'where ',
'--i.GI_REF = ''GI125'' ---:P5_GI_IDEA_REF',
'i.GI_REF = c.GI_REF',
'and i.INITIATOR_ID = u.USER_ID',
'and u.USER_DIRECTORATE = d.DIR_ID',
'and u.USER_BUSINESS_AREA = b.BA_ID',
'and u.USER_TEAM = t.TEAM_ID',
'and u.OFFICE = o.OFFICE_ID',
'and i.GI_REF = c.GI_REF',
'AND',
'-- AO',
'c.CATEGORY IN (''Progress Update'', ''Additional Information'')',
'AND',
'-- Filter by benefits tag (checkboxes)',
'',
'(',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,1)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,2)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,3)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,4)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,5)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,6)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'   ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,7)) > 0 THEN 1 ELSE 0 END) > 0',
'    ',
'    OR :P28_BENEFITS_TAG IS NULL',
')',
'',
'AND (NVL(UPPER(:P28_STATUS),UPPER(i.CURRENT_STATUS))) = UPPER(i.CURRENT_STATUS)',
'',
'AND ',
'     -- date between start and end else if it''s null then bring back everything',
'(CASE WHEN TRIM(:P28_START_DATE) IS NOT NULL AND TRIM(:P28_END_DATE) IS NOT NULL AND to_date(:P28_START_DATE, ''dd/mon/yyyy'')  = to_date(i.DATE_LOGGED,''dd/mon/yyyy'') then 1 else',
'            ((CASE WHEN (TRIM(:P28_START_DATE) IS NOT NULL AND TRIM(:P28_END_DATE) IS NOT NULL AND ',
'               (',
'                   ',
'               --    (i.DATE_LOGGED BETWEEN :P28_START_DATE AND :P28_END_DATE)',
'               (to_date(:P28_START_DATE,''dd/mon/yyyy'') <= to_date(i.DATE_LOGGED ,''dd/mon/yyyy'') AND to_date(i.DATE_LOGGED ,''dd/mon/yyyy'') <= to_date(:P28_END_DATE,''dd/mon/yyyy''))',
'               )',
'               )',
'     THEN 1 ',
'     ELSE  (CASE WHEN TRIM(:P28_START_DATE) IS NULL OR TRIM(:P28_END_DATE) IS NULL THEN 1 ELSE 2  END)',
'     END) )',
'     END) = 1',
'  ',
'       -- Directorate of the user below',
'AND',
'(NVL(UPPER(:P28_DIRECTORATE),(SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(u.USER_DIRECTORATE)))) = (SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(u.USER_DIRECTORATE))',
'     ',
'     -- Business area of the user below',
'AND',
'(NVL(TRIM(UPPER(:P28_BUSINESS_AREA)),(SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(u.USER_BUSINESS_AREA)))) = (SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(u.USER_BUSINESS_AREA))',
'     -- Team of the user',
'AND',
'(NVL(UPPER(:P28_TEAM),(SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(u.USER_TEAM)))) = (SELECT UPPER(TEAM_NAME) FROM GI_TEAMS WHERE UPPER(TEAM_ID) = UPPER(u.USER_TEAM))',
'',
'     -- Area Idea Relates To is below',
'AND',
'',
'(',
'NVL(UPPER(:P28_AREA_AFFECTED), i.AREA_AFFECTED) ',
'    = ',
'NVL2(:P28_AREA_AFFECTED, TRIM(UPPER(REPLACE(i.AREA_AFFECTED,''Other'', ''My''))), i.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P28_RELATESTO_BUSINESS_AREA), i.AREA_AFFECTED) ',
'    = ',
'NVL2(:P28_RELATESTO_BUSINESS_AREA, TRIM(UPPER(REPLACE(i.AREA_AFFECTED_DETAIL,''Other'', ''My''))), i.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P28_RELATESTO_OFFICE), i.AREA_AFFECTED) ',
'    = ',
'NVL2(:P28_RELATESTO_OFFICE, TRIM(UPPER(i.AREA_AFFECTED_DETAIL)), i.AREA_AFFECTED)',
'AND',
'NVL(UPPER(:P28_RELATESTO_IT_DEVELOPMENT), i.AREA_AFFECTED) ',
'    = ',
'NVL2(:P28_RELATESTO_IT_DEVELOPMENT, TRIM(UPPER(i.AREA_AFFECTED_DETAIL)), i.AREA_AFFECTED)',
')',
'',
'AND',
'SUBMITTED_DATE IS NOT NULL ',
'',
'UNION',
'',
'SELECT ',
'i.GI_REF,',
'       u.FIRST_NAME  ||  '' ''  || u.LAST_NAME as Initiator,',
'       d.DIR_NAME as Directorate,',
'       b.BA_NAME as Business_Area,',
'       t.TEAM_NAME as Team,',
'       o.OFFICE_NAME as Office,     ',
'       i.DATE_LOGGED as Date_Idea_Logged,',
'       i.AREA_AFFECTED as Area_Affected,',
'       i.AREA_AFFECTED_DETAIL as Area_Affected_Detail,',
'       i.TITLE_OF_IDEA as Idea_Title,',
'       i.IDEA as Idea,',
'       i.BENEFITS_TAGS as Benefits,',
'       i.BENEFITS as Benefits_Further_Details,',
'       i.INITIATOR_INVOLVEMENT as Initiator_Involement,',
'       i.CURRENT_STATUS as Status,',
'       NVL((Select u.first_name || '' '' || u.last_name from GI_USERS u where to_char(u.user_id) = i.ALLOCATED_TO), NULL) as Allocated_TO,',
'      --- (select LOWER(USER_NAME) from GI_USERS a, GI_IDEAS b where GI_REF = ''GI125'' and b.ALLOCATED_TO = a.USER_ID) as Allocated_To,',
'       i.PARTICIPATION_LEVEL as Participation_Level,',
'       i.REVIEW_DATE as Review_Date,',
'       i.INI_COMPLAINT_COUNT as Initial_Complaint_Count, ',
'       i.POST_COMPLAINT_COUNT as Post_Complaint_Count,',
'	i.INI_ERROR_COUNT as Initial_Error_Count, ',
'   i.POST_ERROR_COUNT as Post_Error_Count, ',
'	i.INI_REJECTS as Initial_Rejects, ',
'   i.POST_REJECTS as Post_Rejects,',
'	i.INI_RESOURCE_HOURS as Initial_Resource_Hours,',
'   i.POST_RESOURCE_HOURS as Post_Resource_Hours, ',
'	i.INI_RISK as Initial_Risk,',
'   i.POST_RISK as Post_Risk, ',
'	i.INI_STAFF_SURVEY_RESULTS as Initial_Staff_Survey_Results, ',
'   i.POST_STAFF_SURVEY_RESULTS as Post_Staff_Survey_Result, ',
'	i.INI_COST as Initial_Cost,	',
'	i.POST_COST as Post_Cost, ',
'	i.INI_IMPACT as Initial_Impact, ',
'	i.POST_IMPACT as Post_Impact,',
'       i.VOTE_COUNT as Vote_count,',
'       NULL,',
'       NULL,',
'       NULL,',
'       NULL,',
'       NULL',
'FROM GI_IDEAS i,',
'     GI_USERS u,',
'     GI_DIRECTORATE d,',
'     GI_BUSINESS_AREAS b,',
'     GI_TEAMS t,',
'     GI_OFFICE o',
'     --GI_COMMENTS c',
'where ',
'-- Filter by benefits tag (checkboxes)',
'',
'(',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,1)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,2)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,3)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,4)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,5)) > 0 THEN 1 ELSE 0 END) > 0 OR ',
'  ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,6)) > 0 THEN 1 ELSE 0 END) > 0 OR',
'   ',
'    (CASE WHEN INSTR(UPPER(i.BENEFITS_TAGS), regexp_substr( '':'' || UPPER(:P28_BENEFITS_TAG) || '':'', ''[^:]+'',1,1)) IS NOT NULL AND ',
'      INSTR(UPPER(BENEFITS_TAGS), regexp_substr(UPPER(:P28_BENEFITS_TAG), ''[^:]+'',1,7)) > 0 THEN 1 ELSE 0 END) > 0',
'    ',
'    OR :P28_BENEFITS_TAG IS NULL',
')',
'',
'AND (NVL(UPPER(:P28_STATUS),UPPER(i.CURRENT_STATUS))) = UPPER(i.CURRENT_STATUS)',
'',
'AND ',
'     -- date between start and end else if it''s null then bring back everything',
'(CASE WHEN TRIM(:P28_START_DATE) IS NOT NULL AND TRIM(:P28_END_DATE) IS NOT NULL AND to_date(:P28_START_DATE, ''dd/mon/yyyy'')  = to_date(i.DATE_LOGGED,''dd/mon/yyyy'') then 1 else',
'            ((CASE WHEN (TRIM(:P28_START_DATE) IS NOT NULL AND TRIM(:P28_END_DATE) IS NOT NULL AND ',
'               ((i.DATE_LOGGED BETWEEN :P28_START_DATE AND :P28_END_DATE))',
'               )',
'     THEN 1 ',
'     ELSE  (CASE WHEN TRIM(:P28_START_DATE) IS NULL OR TRIM(:P28_END_DATE) IS NULL THEN 1 ELSE 2  END)',
'     END) )',
'     END) = 1',
'     ',
'  ',
'       -- Directorate of the user below',
'AND',
'(NVL(UPPER(:P28_DIRECTORATE),(SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(u.USER_DIRECTORATE)))) = (SELECT UPPER(DIR_NAME) FROM GI_DIRECTORATE WHERE DIR_ID = UPPER(u.USER_DIRECTORATE))',
'     ',
'     -- Business area of the user below',
'AND',
'(NVL(TRIM(UPPER(:P28_BUSINESS_AREA)),(SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(u.USER_BUSINESS_AREA)))) = (SELECT UPPER(BA_NAME) FROM GI_BUSINESS_AREAS WHERE BA_ID = UPPER(u.USER_BUSINESS_AREA))',
'   ',
'   -- Team of the user',
